interface LanguageContextType {
  language: Language;
  setLanguage: (lang: Language) => void;
  t: (key: string) => string;
}

const LanguageContext = createContext<LanguageContextType | undefined>(undefined);

type LanguageProviderProps = {
  children: React.ReactNode;
  initialLanguage?: Language;
};

export function LanguageProvider({ children, initialLanguage }: LanguageProviderProps) {
  const getInitialLanguage = (): Language => {
    if (initialLanguage && ['en', 'fr', 'ar'].includes(initialLanguage)) {
      return initialLanguage;
    }
    if (typeof window === 'undefined') {
      return 'en';
    }
    const pathLang = window.location.pathname.match(/^\/(en|fr|ar)(\/|$)/)?.[1];
    if (pathLang) {
      return pathLang as Language;
    }
    const cookieMatch = document.cookie.match(/locale=(en|fr|ar)/);
    if (cookieMatch) {
      return cookieMatch[1] as Language;
    }
    const saved = localStorage.getItem('wayo-language') as Language;
    if (saved && ['en', 'fr', 'ar'].includes(saved)) {
      return saved;
    }
    return 'en';
  };
  
  const [language, setLanguage] = useState<Language>(getInitialLanguage);

  const handleSetLanguage = (lang: Language) => {
    setLanguage(lang);
    localStorage.setItem('wayo-language', lang);
    document.cookie = `locale=${lang};path=/;max-age=31536000`;
  };

  const t = (key: string): string => {
    if (translations[key]) {
      return translations[key][language] || translations[key].en;
    }
    return key;
  };

  return (
    <LanguageContext.Provider value={{ language, setLanguage: handleSetLanguage, t }}>
      {children}
    </LanguageContext.Provider>
  );
}

export function useLanguage() {
  const context = useContext(LanguageContext);
  if (!context) {
    throw new Error('useLanguage must be used within LanguageProvider');
  }
  return context;
}
